@use "mixins" as *;
@use "variables" as *;
@use "mixins" as *;



@include set-root-css() {
   @each $key, $color-scale in $colors {
      @each $color, $value in $color-scale {
         --#{$key}-#{$color}: #{$value};
      }

      @each $color, $value in $color-scale {
         .text-color-#{$color} {
            color: var(--#{$key}-#{$color});
         }
      }

      @each $color, $value in $color-scale {
         .bg-color-#{$color} {
            background-color: var(--#{$key}-#{$color});
         }
      }
   }
}

@include set-root-css() {
   @each $key, $font-scale in $font-sizes {

      @include media-breakpoint($key) {
         @each $size, $value in $font-scale {
            --fs-#{$size}: #{$value};
         }
      }


      @each $size, $value in $font-scale {
         .text-#{$size} {
            font-size: var(--fs-#{$size});
         }
      }
   }
}

@include set-root-css() {
   @for $i from 0 through 10 {
      --space-#{$i}: #{$i * 0.25}rem;
   }
}

@include set-root-css() {
   @for $i from 1 through 6 {
      .img-#{$i} {
         width: 4rem;
         height: 4rem;
         background-image: url("/assets/images/image-#{$i}.png");
         background-position: center;
         background-size: contain;
         background-repeat: no-repeat;
         background-color: var(--primary-500);
         position: absolute;

         @include media-breakpoint(mobile) {
            width: 3rem;
            height: 3rem;
         }

         @if $i ==1 {
            top: 0;
            right: 50%;
            transform: translateX(50%);
            margin-top: -7%;
            z-index: 1;
         }

         @if $i ==2 {
            bottom: 0;
            right: 50%;
            transform: translateX(50%);
            margin-bottom: -8%;
            z-index: 1;
         }

         @if $i ==3 {
            left: 0;
            bottom: 50%;
            transform: translateY(50%);
            margin-left: -8%;
         }

         @if $i ==4 {
            right: 0;
            bottom: 50%;
            transform: translateY(50%) rotate(-90deg);
            margin-right: -8%;
         }

         @if $i ==5 {
            left: 0;
            transform: translate(40%, 40%);
         }

         @if $i ==6 {
            right: 0;
            transform: translate(-50%, 40%) scale(1.3);
            border-radius: 50%;
         }
      }
   }
}

